definition.script:
pipeline {
    agent any

    parameters {
        string(name: 'IMAGE_TAG', description: 'Image tag of the new version of operator to be installed', trim: true, defaultValue: '0.435.67')
        string(name: 'CHART_VERSION', description: 'Version of operator chart to be installed', trim: true, defaultValue: '2.3.4')
        string(name: 'CHART_URL', description: 'URL to download the operator chart', trim: true, defaultValue: 'https://confluent-for-kubernetes.s3-us-west-1.amazonaws.com/confluent-for-kubernetes-2.3.4.tar.gz')
        string(name: 'CHART_LOCATION', description: 'URL to download the operator chart', trim: true, defaultValue: '/var/lib/jenkins/charts')
    }

    stages {
        stage('Check Image') {
            steps {
                dir("${env.BUILD_NUMBER}") {
                    sh """
                        curl --silent -f --head -lL https://hub.docker.com/v2/repositories/confluentinc/confluent-operator/tags/${params.IMAGE_TAG}/ > /dev/null
                        if [ \$? -eq 0 ]; then
                            echo "confluentinc/confluent-operator:${params.IMAGE_TAG} image exists."
                        else
                            echo "Error: Image confluentinc/confluent-operator:${params.IMAGE_TAG} doesn't exist."
                            exit 1
                        fi
                    """
                }
            }
        }

        stage('Download Chart') {
            steps {
                dir("${env.BUILD_NUMBER}") {
                    sh """
                        wget ${params.CHART_URL} --output-document=confluent-for-kubernetes.tar.gz -q
                        if [ \$? -eq 0 ]; then
                            echo "Chart downloaded successfully."
                        else
                            echo "Error: Download failed. Given URL: ${params.CHART_URL}"
                            exit 1
                        fi
                        mkdir -p ${params.CHART_LOCATION}
                        tar zxf confluent-for-kubernetes.tar.gz --strip-components=1 --directory ${params.CHART_LOCATION}
                    """
                }
            }
        }

        stage('Check Chart Version') {
            steps {
                dir("${params.CHART_LOCATION}") {
                    sh """
                        chart_version=\$(helm show chart ./helm/confluent-for-kubernetes | yq '.appVersion')
                        if [[ "\$chart_version" == "${params.CHART_VERSION}" ]]; then
                            echo "${params.CHART_VERSION} check successful."
                        else
                            echo "Chart versions are not equal."
                            echo "Chart version in downloaded chart: \$chart_version."
                            echo "Chart version in parameters: ${params.CHART_VERSION}."
                            exit 1
                        fi
                    """
                }
            }
        }

        stage('Check Operator Version') {
            steps {
                dir("${params.CHART_LOCATION}") {
                    sh """
                        op_version=\$(helm show chart ./helm/confluent-for-kubernetes | yq '.version')
                        if [[ "\$op_version" == "${params.IMAGE_TAG}" ]]; then
                            echo "Operator version check successful. ${params.IMAGE_TAG}"
                        else
                            echo "Operator versions are not equal."
                            echo "Operator version in downloaded chart: \$op_version."
                            echo "Operator version in parameters: ${params.IMAGE_TAG}."
                            exit 1
                        fi
                    """
                }
            }
        }
    }
}
